import { HttpClient } from '@angular/common/http';

// CONFIG
import { environment } from '../../../environments/environment';

// MODEL
import { Professeure } from '../../domain/gestionuniversity_db/professeure';

/**
 * THIS SERVICE MAKE HTTP REQUEST TO SERVER, FOR CUSTOMIZE IT EDIT ../professeure.service.ts
 */

/*
 * SCHEMA DB professeure
 *
	{
		cin: {
			type: 'Number'
		},
		email: {
			type: 'String'
		},
		matiere: {
			type: 'Integer'
		},
		nom: {
			type: 'String'
		},
		num: {
			type: 'Number'
		},
		password: {
			type: 'String'
		},
		prenom: {
			type: 'String'
		},
		//RELATIONS
		//EXTERNAL RELATIONS
		prof_exam: {
			type: Schema.ObjectId,
			required : true,
			ref : "exam"
		},
	}
 *
 */
@Injectable()
export class ProfesseureBaseService {

    contextUrl: string = environment.endpoint + '/professeure';
    constructor(
        protected http: HttpClient
        ) { }

    // CRUD METHODS

    /**
    * professeureService.create
    *   @description CRUD ACTION create
    *
    */
    create(item: Professeure): Observable<Professeure> {
        return this.http
            .post<Professeure>(this.contextUrl, item)
            .pipe(map(data => data));
    }

    /**
    * professeureService.delete
    *   @description CRUD ACTION delete
    *   @param ObjectId id Id
    *
    */
    remove(id: string): Observable<void> {
        return this.http
            .delete<void>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * professeureService.get
    *   @description CRUD ACTION get
    *   @param ObjectId id Id resource
    *
    */
    get(id: string): Observable<Professeure> {
        return this.http
            .get<Professeure>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * professeureService.list
    *   @description CRUD ACTION list
    *
    */
    list(): Observable<Professeure[]> {
        return this.http
            .get<Professeure[]>(this.contextUrl)
            .pipe(map(data => data));
    }

    /**
    * professeureService.update
    *   @description CRUD ACTION update
    *   @param ObjectId id Id
    *
    */
    update(item: Professeure): Observable<Professeure> {
        return this.http
            .post<Professeure>(this.contextUrl + '/' + item._id, item)
            .pipe(map(data => data));
    }


    // Custom APIs

}
